mapper.select:
SELECT c.content_id, c.content_type_id, ct.type_name, c.title, c.is_published, c.view_count, c.created_by, a.name as creator_name, c.created_at, c.updated_at FROM content c LEFT JOIN content_type ct ON c.content_type_id = ct.content_type_id LEFT JOIN admin a ON c.created_by = a.admin_id WHERE 1=1
AND (c.title LIKE CONCAT('%', #{keyword}, '%') OR c.content LIKE CONCAT('%', #{keyword}, '%'))
AND c.content_type_id = #{contentTypeId}
AND c.is_published = #{isPublished}
ORDER BY c.created_at DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM content c WHERE 1=1
AND (c.title LIKE CONCAT('%', #{keyword}, '%') OR c.content LIKE CONCAT('%', #{keyword}, '%'))
AND c.content_type_id = #{contentTypeId}
AND c.is_published = #{isPublished}
