mapper.select:
SELECT id, original_api_id AS originalApiId, title, writer_name AS writerName, written_date AS writtenDate, is_show AS isShow, admin_memo AS adminMemo, is_confirmed AS isConfirmed, confirm_status AS confirmStatus, confirmed_by AS confirmedBy, confirmed_at AS confirmedAt, confirm_memo AS confirmMemo, fetched_at AS fetchedAt, created_at AS createdAt, updated_at AS updatedAt FROM festivals
AND (title LIKE CONCAT('%', #{keyword}, '%') OR content_html LIKE CONCAT('%', #{keyword}, '%') OR writer_name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY written_date DESC, created_at DESC LIMIT #{offset}, #{size}

mapper.update:
UPDATE festivals
SET is_show = #{isShow}
WHERE id = #{id}

UPDATE festivals
SET admin_memo = #{adminMemo}
WHERE id = #{id}
