mapper.select:
SELECT id, username, password, name, email, phone, status, created_at, updated_at
FROM qr_manage_shop_admin
WHERE username = #{username}

SELECT id, username, password, name, email, phone, status, created_at, updated_at
FROM qr_manage_shop_admin
WHERE id = #{id}

SELECT id, username, password, name, email, phone, status, created_at, updated_at
FROM qr_manage_shop_admin
WHERE email = #{email}

mapper.update:
UPDATE qr_manage_shop_admin SET name = #{name}, email = #{email}, phone = #{phone},
password = #{password},
updated_at = NOW() WHERE id = #{id}
