mapper.select:
SELECT s.*, sa.name as shop_admin_name, sa.email as shop_admin_email
FROM qr_manage_shop s
LEFT JOIN qr_manage_shop_admin sa ON s.shop_admin_id = sa.id


        AND s.status = #{status}


        AND s.is_visible = #{isVisible}


        AND (s.name LIKE CONCAT('%', #{keyword}, '%') OR s.address LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY s.created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM qr_manage_shop s


        AND s.status = #{status}


        AND s.is_visible = #{isVisible}


        AND (s.name LIKE CONCAT('%', #{keyword}, '%') OR s.address LIKE CONCAT('%', #{keyword}, '%'))

mapper.update:
UPDATE qr_manage_shop
SET status = #{status}, updated_at = NOW()
WHERE id = #{id}

UPDATE qr_manage_shop
SET is_visible = #{isVisible}, updated_at = NOW()
WHERE id = #{id}
