mapper.select:
SELECT c.content_id, c.content_type_id, ct.type_name, c.title, c.view_count, c.created_at, c.updated_at FROM content c LEFT JOIN content_type ct ON c.content_type_id = ct.content_type_id WHERE c.is_published = TRUE
AND (c.title LIKE CONCAT('%', #{keyword}, '%') OR c.content LIKE CONCAT('%', #{keyword}, '%'))
AND c.content_type_id = #{contentTypeId}
ORDER BY c.created_at DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM content c WHERE c.is_published = TRUE
AND (c.title LIKE CONCAT('%', #{keyword}, '%') OR c.content LIKE CONCAT('%', #{keyword}, '%'))
AND c.content_type_id = #{contentTypeId}

mapper.update:
UPDATE content
SET view_count = view_count + 1
WHERE content_id = #{contentId}
