mapper.select:
SELECT id, original_api_id AS originalApiId, title, category_name AS categoryName, cover_image_url AS coverImageUrl, start_date AS startDate, end_date AS endDate, time_info AS timeInfo, location_name AS locationName, organizer_info AS organizerInfo, tel_number AS telNumber, pay_info AS payInfo, status_info AS statusInfo, division_name AS divisionName, fetched_at AS fetchedAt, created_at AS createdAt, updated_at AS updatedAt FROM exhibitions WHERE is_show = TRUE
AND (title LIKE CONCAT('%', #{keyword}, '%') OR location_name LIKE CONCAT('%', #{keyword}, '%') OR organizer_info LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY start_date DESC, created_at DESC LIMIT #{offset}, #{size}
